import React, { Component } from 'react'
import styles from '../../style/Admin.module.less'

import { Menu, Dropdown, Icon, message } from 'antd';

const onClick = ({ key }) => {
	message.info(`Click on item ${key}`);
};

const menuData = [
	{ path: '', key: '1', name: '个人信息', icon: 'user' },
	{ path: '', key: '2', name: '个人设置', icon: 'user' },
	{ path: '', key: '3', name: '退出登录', icon: 'user' },
]

const menu = (
	<Menu onClick={onClick} {...menuData.map((item,index)=>{
		return(
			<Menu.Item key={item.key}>{item.name}</Menu.Item>
		)
	})}>
	</Menu>
)

class HeaderNav extends Component {
	// constructor(){
	// super()
	// this.setState
	// }
	render() {
		return (
			<div className={styles.headerNav}>
				<Dropdown overlay={menu}>
					<span className="ant-dropdown-link" href="javascript()">
						root <Icon type="down" />
					</span>
				</Dropdown>
			</div>
		)
	}
}

export default HeaderNav
