import React, { Component, Fragment } from 'react'
import { getSelections } from '../../api/section'
import styles from '../../style/Section.module.less'

import { Table, Button, Pagination } from 'antd'
const sectionColumns = [
	// {
	// 	title: '部门编号',
	// 	dataIndex: '_id',
	// 	key: '_id',
	// },
	{
		title: '部门名称',
		dataIndex: 'name',
		key: 'name',
	},
	{
		title: '部门负责人',
		dataIndex: 'leader',
		key: 'leader',
	},
	{
		title: '部门职责',
		dataIndex: 'duty',
		key: 'duty',
	},
	{
		title: '部门人数',
		dataIndex: 'peopleCount',
		key: 'peopleCount',
	},
	{
		title: '部门邮箱',
		dataIndex: 'email',
		key: 'email',
	},
	{
		title: '部门电话',
		dataIndex: 'phone',
		key: 'phone',
	},
	{
		title: '数据修改',
		dataIndex: 'update',
		key: 'update',
		render() {
			return (
				<Fragment>
					<Button type="primary" className={styles.btnprimary}>修改</Button>
					<Button type="danger">删除</Button>
				</Fragment>
			)
		}
	},
]

const pageSize = 3;

class SectionCreate extends Component {
	constructor() {
		super()
		this.state = {
			nowPage: 1,//当前页数
			allCount: 0,//总数据条数
			sectionData: [],
		}
	}
	componentDidMount() {
		this.getTableData(1)
	}
	getTableData() {
		// 根据页数获取网络数据
		getSelections(nowPage, pageSize)
			.then((res) => {
				console.log(res)
				this.setState({ sectionData: res.list.sections,allCount:res.list.allCount })
			})
	}

	render() {
		let { sectionData } = this.state
		return (
			<div>
				<Table className={styles.table} dataSource={sectionData} columns={sectionColumns} pagination={false} rowKey='_id' />
				<Pagination className={styles.antPagination} showQuickJumper defaultCurrent={1} total={20} pageSize={4} />
			</div>
		)
	}
}
export default SectionCreate
