// 封装 ajax
function xhrGet(url) {
    let xhr = new XMLHttpRequest()
    xhr.open('GET', url)
    xhr.send(null)
    let res = ''
    xhr.onreadystatechange = function () {
        if (xhr.readyState === 4 && xhr.status === 200) {
            return res = xhr.responseText
        }
    }
    
}


// 请求天气接口
export const getWeatherDate = async () => {
    let url = '/weather/mojiweather/forecast.php'
    let result = await xhrGet(url)
    return result
}
