import axios from '../utils/axios'

// 部门管理
export const getSelections = async () => {
    let url = `/hehe/v1/admin/section/getSections`
    let uid = getItem('_id') || ''
    let result = await axios.post(url, { uid })
    if (result.err === 0) {
        return result
    } else {
        throw result
    }
}
