// 引入 axios
import axios from '../utils/axios'

// 请求天气接口
export const getWeatherDate = async () => {
    let url = '/weather/mojiweather/forecast.php'
    let result = await axios.get(url)
    if (result.code === 0) {
        // 表示请求数据成功
        return result
    } else {
        // 反之抛出错误
        throw result
    }
}
