import React, { Component } from 'react'
import styles from '../../style/Admin.module.less'

import { Menu, Dropdown, Icon, message, Button } from 'antd';


function handleMenuClick(e) {
	message.info('Click on menu item.');
	console.log('click', e);
}

const menuData = [
	{ path: '', key: '1', name: '个人信息', icon: 'user' },
	{ path: '', key: '2', name: '个人设置', icon: 'user' },
	{ path: '', key: '3', name: '退出登录', icon: 'user' },
]

