// 			3rd item
// 	  </Menu.Item>
// 	</Menu>
// )


class HeaderNav extends Component {
	// constructor(){
	// super()
	// this.setState
	// }
	render() {
		return (
			<div className={styles.headerNav}>
				<Dropdown overlay={menuData.map((item, index) => {
					return (
						<Menu.Item key={item.index}>
							<Icon type={item.icon} />
							<span>{item.name}</span>
						</Menu.Item>
					)
				})}>
					<Button>
						root <Icon type="down" />
					</Button>
				</Dropdown>
			</div>
		)
	}
}

export default HeaderNav
